;
(function($) {
    $.fn.d_fixedMenu = function(t) {
        var u = {
            top: 0,
            space: 1
        };
        $.extend(u, t);
        return this.each(function() {
            var p = $(window);
            var q = $(this);
            var r;
            var s = q.offset().top - u.top;
            setInit();

            function setInit() {
                 
                setScroll()
            }

            function setScroll() {
                p.scroll(function() {
                    r = p.scrollTop();
                    if (r >= s) {
                        if (!q.hasClass('fixed')) {
                            q.addClass('fixed');
                            if (u.space) {
                                q.after('<div class="d_fixed_after" style="height:' + q.height() + 'px"></div>')
                            }
                        }
                    } else {
                        if (q.hasClass('fixed')) {
                            q.removeClass('fixed');
                            if (u.space) {
                                q.next('.d_fixed_after').remove()
                            }
                        }
                    }
                })
            }
        })
    }
})(jQuery);